package com.training.operators;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int FirstNo=20;
		int SecondNo=10;
		System.out.println("The First Number: "+FirstNo);
		System.out.println("The Second Number: "+SecondNo);
		FirstNo += SecondNo; //20+10 =30
		System.out.println("Addition: "+FirstNo); //30
		FirstNo -= SecondNo; //30-10 = 20
		System.out.println("Subtraction: "+FirstNo);
		FirstNo *= SecondNo; //20*10 = 200
		System.out.println("Multiplication: "+FirstNo);

	}
//        /=, %=, , &=, |=, ^=, >>=, <<=

}
